import React, { Component } from 'react';

class SignUp extends Component {

    componentDidMount(){
        
let c, ctx, W, H;
let dots = [];

const random = (max=1, min=0) => Math.random() * (max - min) + min;

class Dot {
   constructor(a){    
      this.x = random(W)
      this.y = random(H)
      this.r = 1.5
      this.s = { x:random(1,-1),y:random(1,-1)}
      this.dir = {x:1,y:1}
   }
   draw() {
      ctx.beginPath()
      ctx.fillStyle = 'white'
      ctx.arc(this.x, this.y, this.r, 0, 2 * Math.PI)
      ctx.fill()
   }
   update() {
      if(this.x>W-this.r||this.x<this.r)this.dir.x*=-1
      if(this.y>H-this.r||this.y<this.r)this.dir.y*=-1
      this.x += this.s.x*this.dir.x
      this.y += this.s.y*this.dir.y
      this.draw()
   }
}

const updateDots = ()=> {
   for(let i=0; i<dots.length; i++){    
      dots[i].update();    
      for(let j=i; j<dots.length; j++){    
            let d = Math.hypot(dots[i].x - dots[j].x,dots[i].y - dots[j].y)
            if(d<60&&i!==j){
               ctx.beginPath()
               ctx.strokeStyle = 'rgba(0,255,200,' + 20/d + ')'
               ctx.lineWidth =  1 
               ctx.moveTo(dots[i].x, dots[i].y);
               ctx.lineTo(dots[j].x, dots[j].y);
               ctx.stroke();
            }      
      }  
   }
}

const init = () => {
   c = document.getElementById("cnv");
   c.width = W = window.innerWidth;
   c.height = H = window.innerHeight;
   ctx = c.getContext("2d");
   for(let i=0;i<200;i++) dots.push(new Dot())
   animate();
};

const animate = () => {
   ctx.clearRect(0, 0, W, H);
   updateDots();
   requestAnimationFrame(animate);
};

// onload = init;

    }

  render() {
    return (
        <div class="row myrow">
            <div class="col-md-6">
                <img src="images/connect.png" alt="" class="logo"/>
                
                <p class="desc">Lorem ipsum dolor sit amet consectetur adipisicing elit. Qui rerum ex eius tenetur aliquam, facere itaque minima cum voluptate vero accusamus quia, nobis, vitae quasi at ad. Officiis, molestias odio.</p>
                <canvas id="cnv"></canvas>
            </div>

            <div class="col-md-6">
                <form action="" class="right">
                    <h1 class="heading">Sign Up</h1>
                    <p>Connect with the awesome world inside Jamia</p>
                    <br/>
                    <input type="text" class="fname" placeholder="First Name"/>
                    <input type="text" class="lname" placeholder="Last Name"/>
                    <br/>
                    <input type="email" placeholder="Email Address"/>
                    <br/>
                    <input type="password" placeholder="Password"/>
                    <br/>
                    <label for="" class="one">Gender</label>
                    <div class="two">
                    <input type="radio" id="male" name="gender" value="male"/>
                    <label for="male">Male</label>
                    </div>
                    <div class="three">
                    <input type="radio" id="female" name="gender" value="female"/>
                    <label for="female">Female</label>
                    </div>
                            
                    <br/>
                    <input type="date" id="dob" name="dob" value=""/>
                    <label for="dob">Date of Birth</label>
                    <br/>
                    <select name="branch" id="branch" class="branch">
                    <option value="cse">Computer Sciences Engineering</option>
                    <option value="ec">Electronics Engineering</option>
                    <option value="ee">Electrical Engineering</option>
                    <option value="me">Mechanical Engineering</option>
                    <option value="ce">Civil Engineering</option>
                    </select>
                    <br/>
                    <label for="Semester"></label>
                    <select name="semester" id="semester" class="branch">
                    <option value="1">Semester I</option>
                    <option value="2">Semester II</option>
                    <option value="3">Semester III</option>
                    <option value="4">Semester IV</option>
                    <option value="5">Semester V</option>
                    <option value="6">Semester VI</option>
                    <option value="7">Semester VII</option>
                    <option value="8">Semester VIII</option>
                    <option value="alm">Passout / Alumni</option>

                    </select>
                    <br/>

                    

                    <button type="submit">Sign Up</button>
                    <br/>
                    <a class="loginuser" href="login.html">Already a User? Log In Instead</a>

                </form>
            </div>
        </div>
    )
    
}
}


export default SignUp;
